Trigger.script:
local affs = { "weakness", "amnesia" }
if matches[1]:find("weakened") then
	affs = { "weakness" }
elseif matches[1]:find("amnesiac state")
	or matches[1]:find("clutches at")
then
	affs = { "amnesia" }
end
system.hunting.funcs.handleRageHit(matches[2], matches[3], "synergy", false, nil, affs)
