Trigger.script:
local affs = {"sensitivity", "stun"}
if matches[1]:find("already vulnerable") then
	affs = { "sensitivity" }
elseif not matches[1]:find("unresponsive form") then
	affs = { "stun" }
end

if not matches[3] then
	matches[3] = string.lower(system.hunting.vars.targetName)
end
system.hunting.funcs.handleRageHit(matches[2], matches[3], "synergy", false, nil, affs)
