Trigger.script:
local affs = {"clumsiness", "recklessness"}
if matches[1]:find("helpless form") then
	affs = { "clumsiness" }
elseif not matches[1]:find("over confident") then
	affs = { "recklessness" }
end
system.hunting.funcs.handleRageHit(string.lower(matches[2]), matches[3], "synergy", false, nil, affs)
