Trigger.script:
local affs = { "aeon", "amnesia" }
if matches[1]:find("slowness") and matches[1]:find("headache") then
	affs = { "aeon" }
elseif matches[1]:find("forgetfulness") and matches[1]:find("headache") then
	affs = { "amnesia" }
end
system.hunting.funcs.handleRageHit(matches[2], matches[3], "synergy", false, nil, affs)
